(function(){var e=io.connect("/");e.on("connect",function(e){console.log("connect",e);$("body").cls("connected")});e.on("disconnect",function(e){console.log("disconnect");$("body").cls({connected:null})});e.on("saveAllData",function(e){$.storage("allData",e)});$("#menu button").tap(function(){var t=$(this);if(t.data("name")==="select"){e.emit("select");$.http.get("/api/user/selected").on({complete:function(e){$("#contents").prepend($.tag("p").text([e.name,e.id].join(",")))}})}else t.data("name")==="currect"?e.emit("currectUser"):e.emit("changePage",{name:t.data("name"),option:t.data("option")})})})();